if 16*SMMLV > contract.wage >= 4*SMMLV:
    result = contract.wage * 0.01
elif 17*SMMLV > contract.wage >= 16*SMMLV:
    result = contract.wage * 0.012
elif 18*SMMLV > contract.wage >= 17*SMMLV:
    result = contract.wage * 0.014
elif 19*SMMLV > contract.wage >= 18*SMMLV:
    result = contract.wage * 0.016
elif 20*SMMLV > contract.wage >= 19*SMMLV:
    result = contract.wage * 0.018
elif contract.wage >= 20*SMMLV:
    result = contract.wage * 0.02

if 95 >= IUVT:
    result = 0
